sql.where:

    	a.rstatus = s.uid


			 and a.name = #{name} 



			 and a.uid = #{uid} 



			 and a.type = #{type} 




				 and a.ideleted = #{ideleted} 


				 and a.ideleted = 0

mapper.select:

        select a.*,s.name as sname from task_action a ,task_status s 




			order by a.${orderBy}	

				${sooort}




			limit ${startRow}, ${pageSize}

mapper.delete:

        update task_action

        set ideleted = 1

		where uid = #{uid}
